package com.example.roomdatabase_021.view import androidx.compose.foundation.layout.Arrangement import androidx.compose.foundation.layout.Column import androidx.compose.foundation.layout.fillMaxWidth import androidx.compose.foundation.layout.padding import androidx.compose.foundation.rememberScrollState import androidx.compose.foundation.verticalScroll import androidx.compose.material3.Button import androidx.compose.material3.ExperimentalMaterial3Api import androidx.compose.material3.HorizontalDivider import androidx.compose.material3.MaterialTheme import androidx.compose.material3.OutlinedTextField import androidx.compose.material3.Scaffold import androidx.compose.material3.Text import androidx.compose.material3.TopAppBarDefaults import androidx.compose.runtime.Composable import androidx.compose.runtime.rememberCoroutineScope import androidx.compose.ui.Modifier import androidx.compose.ui.graphics.Color import androidx.compose.ui.input.nestedscroll.nestedScroll import androidx.compose.ui.res.dimensionResource import androidx.compose.ui.res.stringResource import androidx.lifecycle.viewmodel.compose.viewModel import com.example.roomdatabase_021.R import com.example.roomdatabase_021.view.route.DestinasiEntry import com.example.roomdatabase_021.view.uicontroller.SiswaTopAppBar import com.example.roomdatabase_021.viewmodel.DetailSiswa import com.example.roomdatabase_021.viewmodel.EntryViewModel import com.example.roomdatabase_021.viewmodel.UIStateSiswa import com.example.roomdatabase_021.viewmodel.provider.PenyediaViewModel import kotlinx.coroutines.launch
@OptIn(ExperimentalMaterial3Api::class) @Composable fun EntrySiswaScreen( navigateBack: () -> Unit, modifier: Modifier = Modifier, viewModel: EntryViewModel = viewModel (factory = PenyediaViewModel.Factory) ){ val coroutineScope = rememberCoroutineScope() val scrollBehavior = TopAppBarDefaults.enterAlwaysScrollBehavior() Scaffold( modifier = Modifier.nestedScroll(scrollBehavior.nestedScrollConnection), topBar = { SiswaTopAppBar( title = stringResource(DestinasiEntry.titleRes), canNavigateBack = true, scrollBehavior = scrollBehavior, navigateUp = navigateBack ) } ) { innerPadding -> EntrySiswaBody( uiStateSiswa = viewModel.uiStateSiswa, onSiswaValueChange = viewModel::updateUiState, onSaveClick = { coroutineScope.launch { viewModel.saveSiswa() navigateBack() } }, modifier = Modifier .padding(innerPadding) .verticalScroll(rememberScrollState()) .fillMaxWidth() ) } }
@Composable fun EntrySiswaBody( uiStateSiswa: UIStateSiswa, onSiswaValueChange:(DetailSiswa) -> Unit, onSaveClick: () -> Unit, modifier: Modifier = Modifier ){ Column( verticalArrangement = Arrangement.spacedBy(dimensionResource(id = R.dimen.padding_large)), modifier = modifier.padding(dimensionResource(id = R.dimen.padding_medium)) ) { FormInputSiswa( detailSiswa = uiStateSiswa.detailSiswa, onValueChange = onSiswaValueChange, modifier = Modifier.fillMaxWidth() ) Button( onClick = onSaveClick, enabled = uiStateSiswa.isEntryValid, shape = MaterialTheme.shapes.small, modifier = Modifier.fillMaxWidth() ) { Text(stringResource(R.string.btn_submit)) } } }
@OptIn(ExperimentalMaterial3Api::class) @Composable fun FormInputSiswa( detailSiswa: DetailSiswa, modifier: Modifier = Modifier, onValueChange: (DetailSiswa) -> Unit = {}, enabled: Boolean = true ){ Column( modifier = modifier, verticalArrangement = Arrangement.spacedBy(dimensionResource(id = R.dimen.padding_medium)) ) { OutlinedTextField( value = detailSiswa.nama, onValueChange = {onValueChange(detailSiswa.copy(nama = it))}, label = {Text(stringResource(R.string.nama))}, modifier = Modifier.fillMaxWidth(), enabled = enabled, singleLine = true ) OutlinedTextField( value = detailSiswa.alamat, onValueChange = {onValueChange(detailSiswa.copy(alamat = it))}, label = {Text(stringResource(R.string.alamat))}, modifier = Modifier.fillMaxWidth(), enabled = enabled, singleLine = true ) OutlinedTextField( value = detailSiswa.telpon, onValueChange = {onValueChange(detailSiswa.copy(telpon = it))}, label = {Text(stringResource(R.string.telpon))}, modifier = Modifier.fillMaxWidth(), enabled = enabled, singleLine = true )
if (enabled){ Text( stringResource(R.string.required), modifier = Modifier.padding(start = dimensionResource(id = R.dimen.padding_medium)) ) } HorizontalDivider(modifier = Modifier.padding(bottom = dimensionResource(id = R.dimen.padding_medium)), thickness = dimensionResource(id = R.dimen.padding_small), color = Color.Blue) } }

